mapper.select:
SELECT
FROM owners WHERE last_name like concat(#{lastName}, '%') order by id desc
limit ${limit}

mapper.insert:
		
			select nextval('seq_owners')
		

		insert into owners (id, first_name, last_name,
		address, city, telephone
		)
		values (#{id,jdbcType=INTEGER}, #{firstName,jdbcType=VARCHAR}, #{lastName,jdbcType=VARCHAR},
		#{address,jdbcType=VARCHAR}, #{city,jdbcType=VARCHAR},
		#{telephone,jdbcType=VARCHAR}
		)
